svg.script:
async function init() {
  // Check if MetaMask (or compatible wallet) is installed
  if (typeof window.ethereum !== 'undefined') {
    try {
      // Request account access
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      if (accounts.length > 0) {
        console.log('Connected to MetaMask with account:', accounts[0]);
        // Update SVG to show success
        document.getElementById('status').textContent = 'Connected!';
      }
    } catch (error) {
      console.error('MetaMask connection failed:', error);
      // Update SVG to show error
      document.getElementById('status').textContent = 'Connection Failed';
    }
  } else {
    console.error('MetaMask not detected');
    // Update SVG to prompt MetaMask installation with a link
    document.getElementById('status').textContent = 'Install MetaMask';
    document.getElementById('status').setAttribute('fill', 'blue');
    // Optionally, wrap text in a link (uncomment to enable)
    /*
    const textElement = document.getElementById('status');
    const link = document.createElementNS('http://www.w3.org/2000/svg', 'a');
    link.setAttributeNS('http://www.w3.org/1999/xlink', 'xlink:href', 'https://metamask.io/download/');
    link.setAttribute('target', '_blank');
    textElement.parentNode.insertBefore(link, textElement);
    link.appendChild(textElement);
    */
  }
}
